import React from 'react';
import { Bar } from 'react-chartjs-2';
import { applyChartJsTheme, CYBER_COLORS } from '../../utils/chartTheme';

interface CyberBarChartProps {
  data: any;
  options?: any;
  height?: number | string;
}

export const CyberBarChart: React.FC<CyberBarChartProps> = ({ data, options = {}, height = 300 }) => {
  const themeOptions = applyChartJsTheme(options);
  return (
  <div className="w-full h-full bg-gradient-to-br from-[#0A0A2F] via-[#1A1A4E] to-[#16213E] rounded-2xl p-4 border-2 border-cyber-primary shadow-[0_0_24px_0_rgba(0,255,255,0.15)] backdrop-blur-lg">
      <Bar data={data} options={themeOptions} height={height} />
    </div>
  );
};

export default CyberBarChart;
