// Declaração para permitir uso de JSX com @react-pdf/renderer sem erro de tipagem
import { DocumentProps, PageProps, TextProps, ViewProps } from '@react-pdf/renderer';

declare module '@react-pdf/renderer' {
  export const Document: React.ComponentType<DocumentProps>;
  export const Page: React.ComponentType<PageProps>;
  export const Text: React.ComponentType<TextProps>;
  export const View: React.ComponentType<ViewProps>;
  export const StyleSheet: any;
}

declare global {
  namespace JSX {
    interface IntrinsicElements {
      Document: DocumentProps;
      Page: PageProps;
      Text: TextProps;
      View: ViewProps;
    }
  }
}
